package java_example_20180516;

public class AssignmentOperaritorExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a = 0;
a += 10;// a = a+10
System.out.println(a);

a-=5;//a=// a-5
System.out.println(a);

a*=3;// a=a *3
System.out.println(a);

a/=5;//a= a/5
System.out.println(a);;

a%=3;//a= a%3
System.out.println(a);
	}

}
