package java_example_20180516;

public class ch3_arithmenticoperation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int v1=5;
int v2=2;
int result1= v1/v2;
System.out.println(result1); //2

int result2= v1 +v2;
System.out.println(result2); //7

int result3= v1- v2;
System.out.println(result3);//3

int result4= v1 * v2;
System.out.println(result4);//10

int result5= v1%v2;//1 나머지값
System.out.println(result5);

double result6= (double) v1/v2;//2.5
System.out.println("result6="+ result6);
System.out.println(result6);
	}

}
