package java_example_20180516;

public class ch3_denylogicoperationExampleka {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
boolean play = true;
System.out.println(play);

//자바에서!는 not을 의미함
play =!play; //f
System.out.println(play);

play=!play;//t
System.out.println(play);

}
}