package java_example_20180517;

public class forexample3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//1+2+3+~+10=55
		int i, sum=0;
				
		for(i=1; i<=10; i++) {
		sum+=i;
		System.out.print(i);
		if(i<=9)
			System.out.print("+");
		else{
			System.out.print("=");
		System.out.print(sum);
		}
				
		}}}



